mapper.select:
SELECT

FROM sys_area a

WHERE a.id = #{id}

SELECT

FROM sys_area a

WHERE a.is_deleted = #{DEL_FLAG_NORMAL}

${sqlMap.dsf}
OR a.id = #{currentUser.office.area.id}
