mapper.update:
UPDATE sys_area SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}

UPDATE sys_area SET 
	is_deleted = #{DEL_FLAG_DELETE}
WHERE id = #{id} OR parent_ids LIKE 	'%,'||#{id}||',%'
